catalog_script_client.script:
function onSubmit() {

	var email = g_form.getValue('requester_email');
	var phone = g_form.getValue("requester_phone");
	var name = g_form.getValue("requester_name");
	var pattern_phone = /^[(]?(\d{3})[)]?[-|\s]?(\d{3})[-|\s]?(\d{4})$/;
	var pattern_email = /^[A-Za-z0-9!#$%&'*+-/=?^_`{|}~]+@([A-Za-z]+)\.([a-z]{2,3}|[a-z]{2,3}.[a-z]{2,3})$/;
	var pattern_name = /^([A-Za-z])\w+\s([A-Za-z])\w+$/;

	if(!pattern_email.test(email)){
		alert("Invaidate Email!!");
		return false;
	}else if(!pattern_phone.test(phone)){
		alert('Phone enter a valid phone number');
		return false;
	}else if(!pattern_name.test(name)){
		alert("Invalidate, Please enter letters only.");
		return false;
	}else{
		return true;
	} 

}
